--
-- Structure de la table `candidatures`
--

DROP TABLE IF EXISTS `candidatures`;
CREATE TABLE IF NOT EXISTS `candidatures` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `prenom` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `email` varchar(150) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `telephone` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `cv` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `lettre` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `date_postulation` datetime DEFAULT NULL,
  `poste` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `consultée` tinyint(1) DEFAULT '0',
  `diplomes` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Chemin du fichier PDF du dernier diplôme',
  `certification` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'Chemin du fichier PDF de la certification (facultatif)',
  `autres_documents` mediumtext COLLATE utf8mb4_unicode_ci COMMENT 'Liste JSON des chemins des fichiers supplémentaires',
  `favori` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=44 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

