  `consultée` tinyint(1) DEFAULT '0',
  `favori` tinyint(1) DEFAULT '0',
  `accept_conditions` tinyint(1) NOT NULL DEFAULT '1',
  `give_trainings` enum('yes','no') COLLATE utf8mb4_unicode_ci NOT NULL,
  `training_modules` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_email` (`email`),
  UNIQUE KEY `unique_phone` (`phone`)
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

